module.exports = (bot) => {
  bot.loadCommand = (commandName) => {
    try {
      const props = require(`../commands/${commandName}`);
      console.log(`Carregando comando: ${props.help.name}.`);
      if (props.init) {
        props.init(bot);
      }
      bot.commands.set(props.help.name, props);
      return false;
    } catch (e) {
      return `Nao foi possivel carregar o comando ${commandName}: ${e}`;
    }
  };

  bot.unloadCommand = async (commandName) => {
    let command;
    if (bot.commands.has(commandName)) {
      command = bot.commands.get(commandName);
    }
    if (!command) return `O comando \`${commandName}\` nao existe!`;
  
    if (command.shutdown) {
      await command.shutdown(bot);
    }
    delete require.cache[require.resolve(`../commands/${commandName}.js`)];
    return false;
  }
  exports.createWallet = function(data, member) {
    data[member.id] = {
          moni: 0,
          nome: member.username,
          statGrana: 1,
          day: -1,
      emprestimo: 0,
      diaemprestimo: -1,
      _knife: 0,
      _9mm: 0,
      _tec9: 0,
      _mp5: 0,
      _ak47: 0,
      _m4: 0,
      _rpg: 0,
      _goggles: 0,
      _colete: 0,
      _jetpack: 0,
      job: -1,
      jobTime: 0,
      roubo: 0,
      preso: 0,
      betW: 0,
      betL: 0,
      recorde: 0,
      roubosW: 0,
      roubosL: 0,
      jobGanhos: 0,
      lojaGastos: 0
      };
  }
};
